mapper.select:
select 

from tb_address
where id in (
  SELECT addressId as id from tb_user_address where userId=#{userId}
)

mapper.update:
UPDATE tb_address set state=0 where id in (
    SELECT addressId as id from tb_user_address where userId=#{userId}
  ) AND state=1;
  update tb_address
  set state=1
  where id = #{id,jdbcType=INTEGER}
